mapper.insert:
/*[회원 등록]createMember*/
        INSERT INTO member_table(member_email, member_password, member_name)
		VALUES (#{memberEmail}, #{memberPassword}, #{memberName})

mapper.select:
/*[전체 회원 조회]findAllMember*/
        Select member_id, member_email, member_password, member_name
        From member_table

/*[이메일로 회원 조회]findMemberByEmail*/
        Select member_id, member_email, member_password, member_name
        From member_table
        WHERE member_email = #{email}

mapper.update:
/*[이메일로 정보 수정]modifyMember*/
        UPDATE member_table
        set member_email = #{memberEmail},
            member_password = #{memberPassword},
            member_name = #{memberName}
        where member_id = #{memberId}

mapper.delete:
/*[이메일로 정보 삭제]deleteMemberByEmail*/
        delete 
        from member_table
        where member_email = #{email}
